import { Component, OnInit, ViewChild } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { AppointmentService } from 'src/app/service/appointment.service';
import { Appointment } from '../../models/appointment.model';
import { MatPaginator } from '@angular/material/paginator';


@Component({
  selector: 'app-doctor-appointments',
  templateUrl: './doctor-appointments.component.html',
  styleUrls: ['./doctor-appointments.component.css']
})
export class DoctorAppointmentsComponent {

  appointments: Appointment[] = [];
  doctorId = 0;
  displayedColumns: string[] = ['userName', 'userEmail', 'appointmentDate', 'appointmentTime', 'bookingDate', 'reason', 'status'];
  dataSource = new MatTableDataSource<Appointment>();

  constructor(private appointmentService: AppointmentService) { }

 @ViewChild(MatPaginator) paginator!: MatPaginator;

  ngOnInit(): void {
   // To set user_id
   let userStr: any = localStorage.getItem('user');
   if (userStr) {
     let user = JSON.parse(userStr);
     this.doctorId = user.userId;
   } else {
     console.error('Dữ liệu người dùng không tìm thấy trong localStorage.');
   }
   this.getAppointments();
  }
  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }
  getAppointments(): void {
    this.appointmentService.getAppointmentsByDoctorId(this.doctorId).subscribe(
      (data: Appointment[]) => {
        this.appointments = data;
        this.dataSource.data = this.appointments;
      },
      (error) => {
        console.error(error);
      }
    );
  }
  
  

  updateStatus(appointmentId:any, status: any): void {
    this.appointmentService.updateStatus(appointmentId, status).subscribe(
      (data) => {
        console.log('Cập nhật tình trạng cuộc hẹn:', data);
        
        window.location.reload();
        alert("Trạng thái đã được cập nhật.")
        // Perform any additional actions upon success
      },
      (error) => {
        console.error('Lỗi cập nhật cuộc hẹn:', error);
        alert("Lỗi cập nhật cuộc hẹn");
        // Handle error scenarios
      }
    );
  }


  
}
<div class="content">

  <div *ngIf="dataSource.data.length > 0; else noAppointments" class="appointments-container">
    <h2>Lịch Hẹn Bác Sĩ</h2>
    <div class="table-container">
      <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

        <!-- User Name Column -->
        <ng-container matColumnDef="userName">
          <th mat-header-cell *matHeaderCellDef> Tên Bệnh Nhân </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.user.user.firstName}} {{appointment.user.user.lastName}} </td>
        </ng-container>

        <!-- Doctor Name Column -->
        <ng-container matColumnDef="userEmail">
          <th mat-header-cell *matHeaderCellDef> Email Bệnh Nhân </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.user.user.email}} </td>
        </ng-container>

        <!-- Appointment Date Column -->
        <ng-container matColumnDef="appointmentDate">
          <th mat-header-cell *matHeaderCellDef> Ngày Hẹn </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.appointmentDate | date}} </td>
        </ng-container>

        <!-- Appointment Time Column -->
        <ng-container matColumnDef="appointmentTime">
          <th mat-header-cell *matHeaderCellDef> Thời Gian Hẹn </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.appointmentTime}} </td>
        </ng-container>

        <!-- Booking Date Column -->
        <ng-container matColumnDef="bookingDate">
          <th mat-header-cell *matHeaderCellDef> Ngày Đặt Lịch </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.bookingDate | date}} </td>
        </ng-container>

        <!-- Reason Column -->
        <ng-container matColumnDef="reason">
          <th mat-header-cell *matHeaderCellDef> Lý Do </th>
          <td mat-cell *matCellDef="let appointment"> {{appointment.reason}} </td>
        </ng-container>

        <!-- Status Column -->
        <ng-container matColumnDef="status">
          <th mat-header-cell *matHeaderCellDef> Trạng Thái </th>
          <td  mat-cell *matCellDef="let appointment" >
            <ng-container  *ngIf="appointment.status === 'PENDING'">
              <button mat-raised-button color="primary" (click)="updateStatus(appointment.appointmentId,'ACCEPTED')">Chấp Nhập</button>
              <button mat-raised-button color="warn" (click)="updateStatus(appointment.appointmentId,'REJECTED')">Từ Chối</button>
            </ng-container>
            <ng-container *ngIf="appointment.status !== 'PENDING'">
              <button style="background-color: #f0f0f0;" mat-raised-button color="basic">{{appointment.status}}</button>
            </ng-container>
          </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
      <mat-paginator [pageSizeOptions]="[4]" showFirstLastButtons></mat-paginator>
    </div>
   
  </div>
 

  <ng-template #noAppointments>
    <div class="noAppointments">
      <h2>Không tìm thấy cuộc hẹn nào.</h2>
    </div>
  </ng-template>

</div>
